mapper.select:
select user_id, user_pw, user_email, user_name, user_nickname, tel, gender, age from user
where user_id like '%${searchWord}%' or user_pw like '%${searchWord}%' or user_email like '%${searchWord}%' or user_name like '%${searchWord}%' or user_nickname like '%${searchWord}%' or tel like '%${searchWord}%' or gender like '%${searchWord}%' or age like '%${searchWord}%'
order by join_date desc limit ${onePageRecord} offset ${offsetIndex}

select user_id, user_pw, user_email, user_name, user_nickname, tel, report_hit, warning_state
from user where report_hit>0 and warning_state='N'
order by join_date desc limit ${onePageRecord} offset ${offsetIndex}

select b.board_num, user_id, title, b.content, date_format(write_date, '%Y-%m-%d') write_date, state, start_date, end_date, max_user, pNum from board b inner join PT_group p on b.board_num=p.board_num left join (select board_num, count(*) pNum from apply_user where user_state="참여중" group by board_num) n on b.board_num=n.board_num where type_num=2
and ( b.user_id like '%${searchWord}%' or b.title like '%${searchWord}%' or b.content like '%${searchWord}%' or p.state like '%${searchWord}%')
order by b.board_num desc limit ${onePageRecord} offset ${offsetIndex}

		select board_num, user_id, title, hit, date_format(write_date,'%Y-%m-%d') write_date, type_num
      	from board where type_num=4
		order by board_num desc limit ${onePageRecord} offset ${offsetIndex}
