mapper.select:
select b.board_num, user_id, title, hit, date_format(write_date, '%Y-%m-%d') write_date, state, start_date, end_date, max_user, keyword, pt_img_file, pNum from board b inner join PT_group p on b.board_num=p.board_num left join (select board_num, count(*) pNum from apply_user where user_state="참여중" group by board_num) n on b.board_num=n.board_num where type_num=2
and (title like '%${searchWord}%' or content like '%${searchWord}%' or user_id like '%${searchWord}%')
and state=#{state}
order by b.board_num desc limit ${onePageRecord} offset ${offsetIndex}

mapper.update:
update board set 
title=#{title}, content=#{content}
where board_num=#{board_num} and user_id=#{user_id}

update PT_group set
keyword=#{keyword}, start_date=#{start_date}, end_date=#{end_date}, max_user=#{max_user}, pt_img_file=#{pt_img_file}
where board_num=#{board_num}

mapper.select:
select a.user_id, board_num, user_state, user_name, gender, age, height, w.weight, user_nickname
from apply_user a 
left join user u on a.user_id=u.user_id
left join (
	select user_id ,weight 
	from weigth where (user_id, write_date) in (
		select user_id, max(write_date) as write_date 
		from weigth group by user_id
		)
	) w on a.user_id=w.user_id
where board_num=#{param1}

select a.user_id, user_nickname, board_num, user_state, gender, age, height, w.weight, profie_img
from apply_user a 
left join user u on a.user_id=u.user_id
left join (
	select user_id ,weight 
	from weigth where (user_id, write_date) in (
		select user_id, max(write_date) as write_date 
		from weigth group by user_id
		)
	) w on a.user_id=w.user_id
where board_num=#{param1} and user_state="참여중";

select count(b.board_num) cnt from board b inner join PT_group p on b.board_num=p.board_num where type_num = 2
and (title like '%${searchWord}%' or content like '%${searchWord}%' or user_id like '%${searchWord}%')
and state=#{state}
